function navegacionFija(){const e=document.querySelector(".header"),t=document.querySelector(".sobre-festival");window.addEventListener("scroll",(function(){window.scrollY>t.clientHeight?e.classList.add("fijo"):e.classList.remove("fijo")}))}function crearGaleria(){const e=document.querySelector(".galeria-imagenes");for(let t=1;t<=16;t++){const o=document.createElement("PICTURE");o.innerHTML=`\n    <source srcset="dist/img/gallery/thumbs/${t}.avif" type="image/avif">\n    <source srcset="dist/img/gallery/thumbs/${t}.webp" type="image/webp">\n    <img loading="lazy" width="200" height="300" src="dist/img/gallery/thumbs/${t}.jpg" alt="imagen galeria">`,o.onclick=function(){mostrarImagen(t)},e.appendChild(o)}}function mostrarImagen(e){const t=document.createElement("PICTURE");t.innerHTML=`\n    <source srcset="dist/img/gallery/full/${e}.avif" type="image/avif">\n    <source srcset="dist/img/gallery/full/${e}.webp" type="image/webp">\n    <img loading="lazy" width="200" height="300" src="dist/img/gallery/full/${e}.jpg" alt="imagen galeria">`;const o=document.createElement("DIV");o.classList.add("modal"),o.onclick=cerrarModal;const n=document.createElement("BUTTON");n.textContent="X",n.classList.add("modal-btn-cerrar"),n.onclick=cerrarModal,o.appendChild(t),o.appendChild(n);const a=document.querySelector("body");a.classList.add("overflow-hidden"),a.appendChild(o)}function cerrarModal(){const e=document.querySelector(".modal");e.classList.add("fade-out"),setTimeout((()=>{e?.remove();document.querySelector("body").classList.remove("overflow-hidden")}),500)}function resaltarEnlace(){document.addEventListener("scroll",(function(){const e=document.querySelectorAll("section"),t=document.querySelectorAll(".navegacion-principal a");let o="";e.forEach((e=>{const t=e.offsetTop,n=e.clientHeight;window.scrollY>=t-n/3&&(o=e.id)})),t.forEach((e=>{e.classList.remove("activo"),e.getAttribute("href")==="#"+o&&e.classList.add("activo")}))}))}function scrollNav(){document.querySelectorAll(".navegacion-principal a").forEach((e=>{e.addEventListener("click",(function(e){e.preventDefault();const t=e.target.getAttribute("href");if("#"===t)window.scrollTo({top:0,behavior:"smooth"});else{document.querySelector(t).scrollIntoView({behavior:"smooth"})}}))}))}document.addEventListener("DOMContentLoaded",(function(){navegacionFija(),crearGaleria(),resaltarEnlace(),scrollNav()}));